		/// <summary>
		/// Patch Number
		/// </summary>
		public ushort PatchNumber { get; set; }

		/// <summary>
		/// Bank number
		/// 0 - 127, GM percussion bank is 128
		/// </summary>
		public ushort Bank { get; set; }

		/// <summary>
		/// Zones
		/// </summary>
		public Zone[] Zones { get; set; }

		/// <summary>
		/// <see cref="M:System.Object.ToString" />
		/// </summary>
