					sourceStream.Position = value * 2;
					position = value;
				}
			}
		}

		/// <summary>
		/// <see cref="P:NAudio.Wave.WaveStream.WaveFormat" />
		/// </summary>
		public override WaveFormat WaveFormat => waveFormat;

		/// <summary>
		/// Clip indicator. Can be reset.
		/// </summary>
